import "./SeminarModal.css";
import Modal from "react-modal";
import { isValid, parseISO, format } from "date-fns";

const SeminarModal = ({
  updateSeminar,
  modalIsOpen,
  closeModal,
  editingSeminar,
  setEditingSeminar,
}) => {
  // Функция для конвертации даты в нужный формат для <input type="date">
  const formatDateForInput = (date) => {
    if (!date || !date.includes(".")) return ""; // Проверяем, есть ли дата и правильный ли формат
    try {
      const [day, month, year] = date.split(".");
      return format(new Date(`${year}-${month}-${day}`), "yyyy-MM-dd");
    } catch (error) {
      console.error("Ошибка преобразования даты:", error);
      return "";
    }
  };

  // Функция для обработки изменений даты
  const handleDateChange = (e, setEditingSeminar) => {
    const newDate = e.target.value;
    if (isValid(parseISO(newDate))) {
      setEditingSeminar((prev) => ({
        ...prev,
        date: format(parseISO(newDate), "dd.MM.yyyy"), // Конвертация обратно
      }));
    } else {
      alert("Некорректная дата!");
    }
  };

  return (
    <Modal
      className="seminar-modal"
      isOpen={modalIsOpen}
      onRequestClose={closeModal}
      bodyOpenClassName="modal-open"
    >
      <h2>Редактирование:</h2>
      {editingSeminar && (
        <div className="seminar-modal__form">
          <label className="seminar-modal__label">
            Название:
            <input
              type="text"
              value={editingSeminar.title}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, title: e.target.value })
              }
            />
          </label>
          <label>
            Описание:
            <textarea
              value={editingSeminar.description}
              onChange={(e) =>
                setEditingSeminar({
                  ...editingSeminar,
                  description: e.target.value,
                })
              }
            />
          </label>
          <label>
            Дата:
            <input
              type="date"
              value={formatDateForInput(editingSeminar.date)}
              onChange={(e) => handleDateChange(e, setEditingSeminar)}
            />
          </label>
          <label>
            Время:
            <input
              type="time"
              value={editingSeminar.time}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, time: e.target.value })
              }
            />
          </label>
          <div className="seminar-modal__btns">
            <button className="seminar-modal__btn" onClick={updateSeminar}>
              Сохранить
            </button>
            <button className="seminar-modal__btn" onClick={closeModal}>
              Закрыть
            </button>
          </div>
        </div>
      )}
    </Modal>
  );
};

export default SeminarModal;
