import React, { useEffect, useState } from "react";
import axios from "axios";
import Modal from "react-modal";

const API_URL = "http://localhost:4000/seminars";

Modal.setAppElement("#root");

const App = () => {
  const [seminars, setSeminars] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [editingSeminar, setEditingSeminar] = useState(null);

  // Получение списка семинаров
  useEffect(() => {
    axios
      .get(API_URL)
      .then((response) => setSeminars(response.data))
      .catch((error) => console.error("Ошибка загрузки данных:", error));
  }, []);

  // Удаление семинара
  const deleteSeminar = (id) => {
    if (window.confirm("Вы уверены, что хотите удалить этот семинар?")) {
      axios
        .delete(`${API_URL}/${id}`)
        .then(() => setSeminars(seminars.filter((s) => s.id !== id)))
        .catch((error) => console.error("Ошибка удаления:", error));
    }
  };

  // Открытие модального окна
  const openModal = (seminar) => {
    setEditingSeminar(seminar);
    setModalIsOpen(true);
  };

  // Закрытие модального окна
  const closeModal = () => {
    setModalIsOpen(false);
    setEditingSeminar(null);
  };

  // Обновление семинара
  const updateSeminar = () => {
    axios
      .put(`${API_URL}/${editingSeminar.id}`, editingSeminar)
      .then(() => {
        setSeminars(
          seminars.map((s) => (s.id === editingSeminar.id ? editingSeminar : s))
        );
        closeModal();
      })
      .catch((error) => console.error("Ошибка обновления:", error));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>📅 Список семинаров</h1>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
          gap: "20px",
        }}
      >
        {seminars.map((seminar) => (
          <div
            key={seminar.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "10px",
              textAlign: "center",
            }}
          >
            <img
              src={seminar.photo}
              alt={seminar.title}
              style={{ width: "100%", borderRadius: "10px" }}
            />
            <h3>{seminar.title}</h3>
            <p>{seminar.description}</p>
            <p>
              <strong>
                📅 {seminar.date} | 🕒 {seminar.time}
              </strong>
            </p>
            <button
              onClick={() => openModal(seminar)}
              style={{ marginRight: "10px" }}
            >
              ✏️ Редактировать
            </button>
            <button onClick={() => deleteSeminar(seminar.id)}>
              🗑️ Удалить
            </button>
          </div>
        ))}
      </div>

      {/* Модальное окно редактирования */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={{
          content: {
            width: "50%",
            margin: "auto",
            padding: "20px",
            borderRadius: "10px",
          },
        }}
      >
        <h2>✏️ Редактирование семинара</h2>
        {editingSeminar && (
          <div>
            <label>Название:</label>
            <input
              type="text"
              value={editingSeminar.title}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, title: e.target.value })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <label>Описание:</label>
            <textarea
              value={editingSeminar.description}
              onChange={(e) =>
                setEditingSeminar({
                  ...editingSeminar,
                  description: e.target.value,
                })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <label>Дата:</label>
            <input
              type="date"
              value={editingSeminar.date}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, date: e.target.value })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <label>Время:</label>
            <input
              type="time"
              value={editingSeminar.time}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, time: e.target.value })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <button onClick={updateSeminar} style={{ marginRight: "10px" }}>
              💾 Сохранить
            </button>
            <button onClick={closeModal}>❌ Закрыть</button>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default App;
