import "./SeminarModal.css";
import Modal from "react-modal";

const SeminarModal = ({
  updateSeminar,
  modalIsOpen,
  closeModal,
  editingSeminar,
  setEditingSeminar,
}) => {
  const formatDateForInput = (dateString) => {
    if (!dateString) return "";
    const parts = dateString.split(".");
    if (parts.length !== 3) return dateString;
    const [day, month, year] = parts;
    return `${year}-${month}-${day}`; // yyyy-MM-dd для input
  };

  const formatDateForDisplay = (dateString) => {
    if (!dateString) return "";
    const parts = dateString.split("-");
    if (parts.length !== 3) return dateString;
    const [year, month, day] = parts;
    return `${day}.${month}.${year}`; // dd.MM.yyyy для отображения
  };
  return (
    <Modal
      className="seminar-modal"
      isOpen={modalIsOpen}
      onRequestClose={closeModal}
      bodyOpenClassName="modal-open"
    >
      <h2>Редактирование:</h2>
      {editingSeminar && (
        <div className="seminar-modal__form">
          <label className="seminar-modal__label">
            Название:
            <input
              type="text"
              value={editingSeminar.title}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, title: e.target.value })
              }
            />
          </label>
          <label>
            Описание:
            <textarea
              value={editingSeminar.description}
              onChange={(e) =>
                setEditingSeminar({
                  ...editingSeminar,
                  description: e.target.value,
                })
              }
            />
          </label>
          <label>
            Дата:
            <input
              type="date"
              value={formatDateForInput(editingSeminar.date)}
              onChange={(e) =>
                setEditingSeminar({
                  ...editingSeminar,
                  date: formatDateForDisplay(e.target.value), // Конвертация обратно
                })
              }
            />
          </label>
          <label>
            Время:
            <input
              type="time"
              value={editingSeminar.time}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, time: e.target.value })
              }
            />
          </label>
          <div className="seminar-modal__btns">
            <button className="seminar-modal__btn" onClick={updateSeminar}>
              Сохранить
            </button>
            <button className="seminar-modal__btn" onClick={closeModal}>
              Закрыть
            </button>
          </div>
        </div>
      )}
    </Modal>
  );
};

export default SeminarModal;
