  return (
    <Modal
      isOpen={modalIsOpen}
      onRequestClose={closeModal}
      style={{
        content: {
          width: "50%",
          margin: "auto",
          padding: "20px",
          borderRadius: "10px",
        },
      }}
    >
      <h2>‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å–µ–º–∏–Ω–∞—Ä–∞</h2>
      {editingSeminar && (
        <div>
          <label>–ù–∞–∑–≤–∞–Ω–∏–µ:</label>
          <input
            type="text"
            value={editingSeminar.title}
            onChange={(e) =>
              setEditingSeminar({ ...editingSeminar, title: e.target.value })
