import React, { useEffect, useState } from "react";
import axios from "axios";
import Modal from "react-modal";

const API_URL = "http://localhost:4000/seminars"; // API JSON Server

Modal.setAppElement("#root");

const App = () => {
  const [seminars, setSeminars] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [editingSeminar, setEditingSeminar] = useState(null);

  const [imageUrls, setImageUrls] = useState({});

  useEffect(() => {
    const fetchImages = async () => {
      let urls = {};
      for (const seminar of seminars) {
        try {
          const response = await fetch(
            `https://picsum.photos/id/${seminar.id}/750/730`
          );
          const blob = await response.blob();
          const localUrl = URL.createObjectURL(blob);
          urls[seminar.id] = localUrl;
        } catch (error) {
          console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è:", error);
        }
      }
      setImageUrls(urls);
    };

    fetchImages();
  }, [seminars]);

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Å–ø–∏—Å–∫–∞ —Å–µ–º–∏–Ω–∞—Ä–æ–≤
  useEffect(() => {
    axios
      .get(API_URL)
      .then((response) => setSeminars(response.data))
      .catch((error) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:", error));
  }, []);

  // –£–¥–∞–ª–µ–Ω–∏–µ —Å–µ–º–∏–Ω–∞—Ä–∞
  const deleteSeminar = (id) => {
    if (window.confirm("–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ—Ç —Å–µ–º–∏–Ω–∞—Ä?")) {
      axios
        .delete(`${API_URL}/${id}`)
        .then(() => setSeminars(seminars.filter((s) => s.id !== id)))
        .catch((error) => console.error("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è:", error));
    }
  };

  // –û—Ç–∫—Ä—ã—Ç–∏–µ –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞
  const openModal = (seminar) => {
    setEditingSeminar(seminar);
    setModalIsOpen(true);
  };

  // –ó–∞–∫—Ä—ã—Ç–∏–µ –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞
  const closeModal = () => {
    setModalIsOpen(false);
    setEditingSeminar(null);
  };

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ —Å–µ–º–∏–Ω–∞—Ä–∞
  const updateSeminar = () => {
    axios
      .put(`${API_URL}/${editingSeminar.id}`, editingSeminar)
      .then(() => {
        setSeminars(
          seminars.map((s) => (s.id === editingSeminar.id ? editingSeminar : s))
        );
        closeModal();
      })
      .catch((error) => console.error("–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è:", error));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>üìÖ –°–ø–∏—Å–æ–∫ —Å–µ–º–∏–Ω–∞—Ä–æ–≤</h1>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
          gap: "20px",
        }}
      >
        {seminars.map((seminar) => {
          // –£–±–∏—Ä–∞–µ–º –ø—Ä–æ–∫—Å–∏ –∏ –∏—Å–ø–æ–ª—å–∑—É–µ–º –ø—Ä—è–º—É—é —Å—Å—ã–ª–∫—É
          const imageUrl = `https://picsum.photos/id/${seminar.id}/750/730`;

          return (
            <div
              key={seminar.id}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "10px",
                textAlign: "center",
              }}
            >
              <img
                src={imageUrls[seminar.id]}
                alt={seminar.title}
                style={{ width: "100%", borderRadius: "10px" }}
                crossOrigin="anonymous" // üî• –†–µ—à–µ–Ω–∏–µ CORS-–ø—Ä–æ–±–ª–µ–º—ã
                onError={(e) => (e.target.src = "/fallback-image.jpg")} // –§–æ–ª–±—ç–∫-–∫–∞—Ä—Ç–∏–Ω–∫–∞
              />
              <h3>{seminar.title}</h3>
              <p>{seminar.description}</p>
              <p>
                <strong>
                  üìÖ {seminar.date} | üïí {seminar.time}
                </strong>
              </p>
              <button
                onClick={() => openModal(seminar)}
                style={{ marginRight: "10px" }}
              >
                ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
              </button>
              <button onClick={() => deleteSeminar(seminar.id)}>
                üóëÔ∏è –£–¥–∞–ª–∏—Ç—å
              </button>
            </div>
          );
        })}
      </div>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={{
          content: {
            width: "50%",
            margin: "auto",
            padding: "20px",
            borderRadius: "10px",
          },
        }}
      >
        <h2>‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å–µ–º–∏–Ω–∞—Ä–∞</h2>
        {editingSeminar && (
          <div>
            <label>–ù–∞–∑–≤–∞–Ω–∏–µ:</label>
            <input
              type="text"
              value={editingSeminar.title}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, title: e.target.value })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <label>–û–ø–∏—Å–∞–Ω–∏–µ:</label>
            <textarea
              value={editingSeminar.description}
              onChange={(e) =>
                setEditingSeminar({
                  ...editingSeminar,
                  description: e.target.value,
                })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <label>–î–∞—Ç–∞:</label>
            <input
              type="date"
              value={editingSeminar.date}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, date: e.target.value })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <label>–í—Ä–µ–º—è:</label>
            <input
              type="time"
              value={editingSeminar.time}
              onChange={(e) =>
                setEditingSeminar({ ...editingSeminar, time: e.target.value })
              }
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <button onClick={updateSeminar} style={{ marginRight: "10px" }}>
              üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
            </button>
            <button onClick={closeModal}>‚ùå –ó–∞–∫—Ä—ã—Ç—å</button>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default App;
