import "./SeminarModal.css";
import Modal from "react-modal";

const SeminarModal = ({
  updateSeminar,
  modalIsOpen,
  closeModal,
  editingSeminar,
  setEditingSeminar,
}) => {
  return (
    <Modal
      className="seminar-modal"
      isOpen={modalIsOpen}
      onRequestClose={closeModal}
      bodyOpenClassName="modal-open"
    >
      <h2>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ:</h2>
      {editingSeminar && (
        <div className="modal-content">
          <h2>‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å–µ–º–∏–Ω–∞—Ä–∞</h2>

          <label>–ù–∞–∑–≤–∞–Ω–∏–µ:</label>
          <input
            type="text"
            value={editingSeminar.title}
            onChange={(e) =>
              setEditingSeminar({ ...editingSeminar, title: e.target.value })
            }
            className="modal-input"
          />

          <label>–û–ø–∏—Å–∞–Ω–∏–µ:</label>
          <textarea
            value={editingSeminar.description}
            onChange={(e) =>
              setEditingSeminar({
                ...editingSeminar,
                description: e.target.value,
              })
            }
            className="modal-textarea"
          />

          <div className="modal-row">
            <div>
              <label>–î–∞—Ç–∞:</label>
              <input
                type="date"
                value={editingSeminar.date}
                onChange={(e) =>
                  setEditingSeminar({ ...editingSeminar, date: e.target.value })
                }
                className="modal-input"
              />
            </div>
            <div>
              <label>–í—Ä–µ–º—è:</label>
              <input
                type="time"
                value={editingSeminar.time}
                onChange={(e) =>
                  setEditingSeminar({ ...editingSeminar, time: e.target.value })
                }
                className="modal-input"
              />
            </div>
          </div>

          <div className="modal-buttons">
            <button className="modal-save-btn" onClick={updateSeminar}>
              üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
            </button>
            <button className="modal-close-btn" onClick={closeModal}>
              ‚ùå –ó–∞–∫—Ä—ã—Ç—å
            </button>
          </div>
        </div>
      )}
    </Modal>
  );
};

export default SeminarModal;
