import { useState, useEffect } from "react";
import axios from "axios";
import SeminarList from "./components/SeminarList";
import EditSeminarModal from "./components/EditSeminarModal";
import "../../../src/"; // –ù–æ–≤—ã–π —Å—Ç–∏–ª—å

const API_URL = "https://api.example.com/seminars";

const App = () => {
  const [seminars, setSeminars] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [editingSeminar, setEditingSeminar] = useState(null);

  useEffect(() => {
    axios
      .get(API_URL)
      .then((response) => setSeminars(response.data))
      .catch((error) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:", error));
  }, []);

  const deleteSeminar = (id) => {
    if (window.confirm("–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ—Ç —Å–µ–º–∏–Ω–∞—Ä?")) {
      axios
        .delete(`${API_URL}/${id}`)
        .then(() => setSeminars(seminars.filter((s) => s.id !== id)))
        .catch((error) => console.error("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è:", error));
    }
  };

  const openModal = (seminar) => {
    setEditingSeminar(seminar);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setEditingSeminar(null);
  };

  const updateSeminar = (updatedSeminar) => {
    axios
      .put(`${API_URL}/${updatedSeminar.id}`, updatedSeminar)
      .then(() => {
        setSeminars(
          seminars.map((s) => (s.id === updatedSeminar.id ? updatedSeminar : s))
        );
        closeModal();
      })
      .catch((error) => console.error("–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è:", error));
  };

  return (
    <div className="app-container">
      <h1 className="title">üöÄ –ì—Ä—è–¥—É—â–∏–µ —Å–µ–º–∏–Ω–∞—Ä—ã</h1>
      <SeminarList
        seminars={seminars}
        onEdit={openModal}
        onDelete={deleteSeminar}
      />
      <EditSeminarModal
        isOpen={modalIsOpen}
        seminar={editingSeminar}
        onClose={closeModal}
        onSave={updateSeminar}
      />
    </div>
  );
};

export default App;
